import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { MuiThemeProvider, createMuiTheme } from 'material-ui/styles';
import "./index.css";

const theme = createMuiTheme({
   typography: {
    "fontFamily": "\"Roboto\", \"Helvetica\", \"Arial\", sans-serif",
    "fontSize": 14,
    "fontWeightLight": 300,
    "fontWeightRegular": 400,
    "fontWeightMedium": 500
   }
});
class Dropdown extends Component {
  state = {
    anchorEl: null,
  };

  handleClick = event => {
    this.setState({ anchorEl: event.currentTarget });
  };

  handleClose = () => {
    this.setState({ anchorEl: null });
  };

  render() {
    const { anchorEl } = this.state;

    return (
      <MuiThemeProvider theme={theme}>
      <div>
        <a
          aria-owns={anchorEl ? 'simple-menu' : null}
          aria-haspopup="true"
          onClick={this.handleClick}
          id="dropdownButton"
        >
          <i className="fas fa-bars"></i>
        </a>
        <Menu
          id="simple-menu"
          anchorEl={anchorEl}
          open={Boolean(anchorEl)}
          onClose={this.handleClose}
        >
          <MenuItem onClick={this.handleClose}><a href="/About">About</a></MenuItem>
            <MenuItem onClick={this.handleClose}><a href="/Speaker">Our Speaker</a></MenuItem>
              <MenuItem onClick={this.handleClose}><a href="/Workshops">Workshops</a></MenuItem>
                <MenuItem onClick={this.handleClose}><a href="/Register">Register</a></MenuItem>
                  <MenuItem onClick={this.handleClose}><a href="/Shirt">T-Shirt Design</a></MenuItem>
                    <MenuItem onClick={this.handleClose}><a href="/Contact">Contact</a></MenuItem>
        </Menu>
      </div>
    </MuiThemeProvider>
    );
  }
}

export default Dropdown;
